import User from '../models/User.js'
import City from '../models/City.js'
import bcrypt from 'bcryptjs'
import jwt from 'jsonwebtoken'
import path from 'path'

/** Регистрация пользователя */
export const register = async (req, res) => {
    try {
        //** Получение параметров и их проверка */
        const {username, password, city} = req.body
        if (username == '' || password == '' || city == '') {
            return res.status(400).json({
                message: 'Заполнены не все поля',
            })
        }
        /** Проверка на username */
        const isUsed = await User.findOne({username})
        if (isUsed) {
            return res.status(409).json({
                message: 'Логин занят. Выберите другой!',
            })
        }
        /** Поиск id города */
        const isCity = await City.findOne({_id: city})
        if (!isCity) {
            return res.status(409).json({
                message: 'Такого города нет',
            })
        }
        const idCity = isCity._id
        /** Пароль */
        const salt = bcrypt.genSaltSync(10)
        const hash = bcrypt.hashSync(password, salt)

        /** Картинка по умолчанию */
        var base = 'data:image/jpeg;base64,'

        /** Создание нового пользователя */
        const newUser = new User({
            username,
            city: idCity,
            password: hash,
            text: '',
            image: base,
            typeImg: 'image/png'
        })

        const token = jwt.sign(
            {
                id: newUser._id,
            },
            '0a6b944d-d2fb-46fc-a85e-0295c986cd9f',
            {expiresIn: '30d'},
        )

        await newUser.save()

        console.log("save")

        res.status(201).json({
            newUser,
            token,
            message: 'Регистрация успешна',
        })
    } catch (error) {
        console.log(error)
        res.status(400).json({message: 'Ошибка при авторизации'})
    }
}

/** Вход пользователя */
export const login = async (req, res) => {
    try {
        /** Параметры и их проверка */
        const {username, password} = req.body
        const user = await User.findOne({username})
        if (!user) {
            return res.status(404).json({
                message: 'Такого пользователя не существует',
            })
        }
        
        /** Проверка пароля */
        const isPasswordCorrect = await bcrypt.compare(password, user.password)
        if (!isPasswordCorrect) {
            return res.status(401).json({
                message: 'Неверный пароль',
            })
        }

        const token = jwt.sign(
            {
                id: user._id,
            },
            "0a6b944d-d2fb-46fc-a85e-0295c986cd9f",
            {expiresIn: "1h"},
        )
        user.password = ""

        res.status(201).json({
            token,
            user,
            message: 'Успешный вход в систему.',
        })
    } catch (error) {
        res.status(400).json({message: 'Ошибка при авторизации.'})
    }
}

